mapper.select:
select
  caigouid, caigouname, gonghuoshang, danwei, candi, leixing, shuliang
from caigou


        and caigouid = #{caigouid}


        and caigouname = #{caigouname}


        and gonghuoshang = #{gonghuoshang}


        and danwei = #{danwei}


        and candi = #{candi}


        and leixing = #{leixing}


        and shuliang = #{shuliang}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from caigou


        and caigouid = #{caigouid}


        and caigouname = #{caigouname}


        and gonghuoshang = #{gonghuoshang}


        and danwei = #{danwei}


        and candi = #{candi}


        and leixing = #{leixing}


        and shuliang = #{shuliang}
