mapper.select:
select
  cid, registerid, content, ctime
from checkup


        and cid = #{cid}


        and registerid = #{registerid}


        and content = #{content}


        and ctime = #{ctime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from checkup


        and cid = #{cid}


        and registerid = #{registerid}


        and content = #{content}


        and ctime = #{ctime}
