mapper.select:
select
  drugId, drugName, unitId, sellingPrice, areaId, typeId
from drugdictionary


        and drugId = #{drugid}


        and drugName = #{drugname}


        and unitId = #{unitid}


        and sellingPrice = #{sellingprice}


        and areaId = #{areaid}


        and typeId = #{typeid}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from drugdictionary


        and drugId = #{drugid}


        and drugName = #{drugname}


        and unitId = #{unitid}


        and sellingPrice = #{sellingprice}


        and areaId = #{areaid}


        and typeId = #{typeid}
