mapper.select:
select
  huishouid, huishouname, huishounumber, huishoupihao, jbr, beizhu
from huishou


        and huishouid = #{huishouid}


        and huishouname = #{huishouname}


        and huishounumber = #{huishounumber}


        and huishoupihao = #{huishoupihao}


        and jbr = #{jbr}


        and beizhu = #{beizhu}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from huishou


        and huishouid = #{huishouid}


        and huishouname = #{huishouname}


        and huishounumber = #{huishounumber}


        and huishoupihao = #{huishoupihao}


        and jbr = #{jbr}


        and beizhu = #{beizhu}
