mapper.select:
select
  jiluid, jiluname, jilutime, jilutype, jilupeople, jilunumber, jilupihao
from jilu


        and jiluid = #{jiluid}


        and jiluname = #{jiluname}


        and jilutime = #{jilutime}


        and jilutype = #{jilutype}


        and jilupeople = #{jilupeople}


        and jilunumber = #{jilunumber}


        and jilupihao = #{jilupihao}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from jilu


        and jiluid = #{jiluid}


        and jiluname = #{jiluname}


        and jilutime = #{jilutime}


        and jilutype = #{jilutype}


        and jilupeople = #{jilupeople}


        and jilunumber = #{jilunumber}


        and jilupihao = #{jilupihao}
