mapper.select:
select
  outpatientId, projectName, unit, bigprojectId, price, ostate
from outpatienttype


        and outpatientId = #{outpatientid}


        and projectName = #{projectname}


        and unit = #{unit}


        and bigprojectId = #{bigprojectid}


        and price = #{price}


        and ostate = #{ostate}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from outpatienttype


        and outpatientId = #{outpatientid}


        and projectName = #{projectname}


        and unit = #{unit}


        and bigprojectId = #{bigprojectid}


        and price = #{price}


        and ostate = #{ostate}
