mapper.select:
select
  roleid, rolename, roledesc, available
from sys_role


        and roleid = #{roleid}


        and rolename = #{rolename}


        and roledesc = #{roledesc}


        and available = #{available}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from sys_role


        and roleid = #{roleid}


        and rolename = #{rolename}


        and roledesc = #{roledesc}


        and available = #{available}
